	def _generate_examples(self, filepaths):
		_id = 0
		for filepath in filepaths:
			df = pd.read_csv(open(filepath, 'rb'), sep = ',', skiprows = 0, error_bad_lines = False, header = None)
			df.columns = ['text']
			for _, record in df.iterrows():
				yield str(_id), {'text':record['text']}
				_id += 1 

